msg.writermap.forEach( (txt) => {
    msg.payload.push( {
        payload: txt.text,
        name: txt.name
    })
})

for ( var nme in msg ) {
    if ( nme.startsWith("js")) {
        msg.payload.push({
            payload: msg[nme],
            name: "src/" + nme.replace(/^js(.)/, function (m) { return m[2].toLowerCase() }) + ".js"
        })
        delete msg[nme];
    }
}

return msg;

/*
  this only commits the js and css files, everything else
  is ignored.
 */
delete msg.writermap;

for (var nme in msg) {
    if (nme.startsWith("js")) {
        delete msg[nme];
    }
}

msg.payload = msg.payload.filter( (obj) => {
    return (obj.name == "flowviewer.css" || obj.name == "flowviewer.js")
}).map( function(obj){
    obj.name = "embed/" + obj.name;
    return obj;
});

return msg;

msg.payload = msg.payload.filter( function(d){
    return d.path == "embed"
})[0].sha;

return msg;

// flowviewer.js

var currSha = msg.payload.filter( function(d){
    return d.path == "flowviewer.js"
})[0].sha;

var newSha = msg.blobs.filter( function(d) {
    return d.path == "embed/flowviewer.js"
})[0].sha;

if ( currSha != newSha ) { return [{...msg, payload: "js differ"}, undefined ] }

// flowviewer.css

currSha = msg.payload.filter(function (d) {
    return d.path == "flowviewer.css"
})[0].sha;

newSha = msg.blobs.filter(function (d) {
    return d.path == "embed/flowviewer.css"
})[0].sha;

if (currSha != newSha) { return [{...msg, payload: "css differ"}, undefined] }

// flowviewer.min.js

currSha = (msg.payload.filter(function (d) {
    return d.path == "flowviewer.min.js"
})[0] || {sha: undefined}).sha;

newSha = (msg.blobs.filter(function (d) {
    return d.path == "embed/flowviewer.min.js"
})[0] || { sha: undefined}).sha;

if (currSha != newSha || currSha == undefined) { return [{...msg, payload: "css differ"}, undefined] }

//
// Else all good, same, same but not different.
//
return [undefined, {...msg, payload: "same"}];

